mapper.select:
SELECT r.room_id, r.room_num, r.room_size, r.people_num, CASE WHEN r.people_num = 0 THEN '未入住' WHEN r.people_num is null THEN '未入住' ELSE CONCAT('已入住',r.people_num,'人') END AS people_num2, CASE WHEN r.room_status = 0 THEN '已启用' WHEN r.room_status = 1 THEN '已住满' WHEN r.room_status = 2 THEN '已停用' END AS room_status, r.room_status room_status2, r.people_rent, r.room_rent, r.room_area, r.start_date, r.end_date, r.created_user, DATE_FORMAT(r.created_date, '%Y-%m-%d %h:%i:%s') as created_date, r.modify_user, DATE_FORMAT(r.modify_date, '%Y-%m-%d %h:%i:%s') as modify_date, r.is_del, b.building_id, b.building_name, a.area_name, a.neighbourhood, l.landlord_id, l.landlord_name, l.landlord_phone, l.landlord_sex, rt.room_type_id, rt.room_type_name FROM room r LEFT JOIN building b ON (r.building_id = b.building_id) LEFT JOIN area a ON (b.area_id = a.area_id) LEFT JOIN landlord l ON (l.landlord_id = r.landlord_id) LEFT JOIN room_type rt ON (r.room_type_id = rt.room_type_id)
a.area_id = #{areaId}
AND r.room_num = #{roomNum}
AND DATE_FORMAT(r.start_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
AND DATE_FORMAT(r.end_date,, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
AND r.people_rent <= #{peopleRent}
AND r.room_type_id = #{roomTypeId}
AND l.landlord_name like concat(concat('%',#{landlordName}),'%')
AND r.room_status = #{roomStatus}
ORDER BY r.created_date DESC

SELECT
  count(*)
FROM
  room


        room_num = #{roomNum}


        AND building_id = #{buildingId}

mapper.update:
UPDATE
    room


        people_num = #{peopleNum, jdbcType = INTEGER},


        is_del = #{isDel, jdbcTye = BIT}


WHERE
  room_id = #{roomId}
