mapper.select:
SELECT u.user_id, u.user_phone, u.password, u.real_name, u.user_addr, u.user_sex AS user_sex2, CASE u.user_sex WHEN 1 THEN '男' WHEN 0 THEN '女' END AS 'user_sex', u.user_age, u.user_card, DATE_FORMAT(usr.end_date, '%Y-%m-%d') as end_date, DATE_FORMAT(usr.start_date, '%Y-%m-%d') as start_date, DATE_FORMAT(u.created_date, '%Y-%m-%d %h:%i:%s') as created_date, r.room_num, r.room_id, r.building_id, b.area_id, b.building_name, a.area_name, a.neighbourhood, u.live_status status_code, CASE u.live_status WHEN 1 THEN '入住中' WHEN 2 THEN '已离宿' WHEN 3 THEN '游离人员' END AS 'live_status' FROM user u LEFT JOIN room_user ru ON(ru.user_id = u.user_id) LEFT JOIN room r ON(ru.room_id = r.room_id) LEFT JOIN building b ON(b.building_id = r.building_id) LEFT JOIN area a ON(a.area_id = b.area_id) LEFT JOIN ( SELECT usr2.start_date, usr2.end_date, usr2.user_id FROM user_stay_record usr2 ORDER BY usr2.end_date DESC, usr2.user_stay_record_id ASC LIMIT 2 ) usr ON(u.user_id = usr.user_id) WHERE u.is_del = 0
AND u.user_id = #{userId}
AND u.user_phone = #{userPhone}
AND u.real_name like concat(concat('%',#{realName}),'%')
AND DATE_FORMAT(usr.start_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
AND DATE_FORMAT(usr.end_date, '%Y-%m-%d') >= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
AND usr.live_status = #{liveStatus}
ORDER BY created_date DESC

SELECT COUNT(*) FROM user u LEFT JOIN room_user ru ON(ru.user_id = u.user_id) LEFT JOIN room r ON(ru.room_id = r.room_id) LEFT JOIN ( SELECT start_date, end_date, user_id FROM user_stay_record ORDER BY end_date DESC LIMIT 1 ) usr ON(u.user_id = usr.user_id) WHERE u.is_del = 0
AND u.user_phone = #{userPhone}
AND u.real_name like concat(concat('%',#{realName}),'%')
AND u.user_card = #{userCard}
AND DATE_FORMAT(usr.start_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
AND DATE_FORMAT(usr.end_date, '%Y-%m-%d') >= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
AND usr.live_status = #{liveStatus}
